<?php    class Categories_model extends CI_Model    {    	public function __construct(){        	parent::__construct();        }        public function getAllCategories(){		    $this->db->select('id,name');		    $query = $this->db->get('categories');			$items[0] = '-- Select Category --';		    if ($query->num_rows() > 0) {	        	foreach($query->result() as $data) {	              $items[$data->id] = $data->name;	        	}	          	$query->free_result();	        	return $items;		    }		}		public function getListCategories($limit,$start,$id){		    $this->db->select('id,name,icon,rank,status');			$this->db->limit($limit,$start);		    if($id != ''){		    	$this->db->where('id', $id);		    }		    $this->db->order_by('id', 'ASC');		    $query = $this->db->get('categories',$limit);			//echo $this->db->last_query();		    return $query->result();		}		public function getField($id,$fieldName){			$cond = 'status = 1';		    $this->db->select($fieldName);		    $this->db->where('id', $id);					    $query = $this->db->get('categories');		    return $query->result();		}		//get number of records via cate id		public function record_count(){			$this->db->from('categories');			return $this->db->count_all_results();		}		public function updateCate($id,$data){			$this->db->where('id', $id);			$this->db->update('categories', $data);			//echo $this->db->last_query();exit;			return true;		}    }?>