<?phpclass members_model extends CI_Model{	public function __construct()	{		parent::__construct();	}	function getAllLocations()	{		$this->db->select('id,name');		$query = $this->db->get('members');		if ($query->num_rows() > 0) {			foreach ($query->result() as $data) {				$items[$data->id] = $data->name;			}			$query->free_result();			return $items;		}	}	//Get fields with input are fields, table via id	public function getField($id, $fieldName, $tbName)	{		$this->db->select($fieldName);		$this->db->where('id', $id);		$query = $this->db->get($tbName);		return $query->result();	}	//Check existing username	public function checkExistuser($fieldId, $fieldName)	{		$this->db->select('id');		$this->db->where($fieldId, $fieldName);		$query = $this->db->get('members');		return $query->result();	}	//add new member	public function addNew($data,$tbName){		$this->db->insert($tbName, $data);		return $this->db->insert_id();	}	//get field via condition	public function get_field( $field_name, $tbname, $wh ){		$returnField = '';		$sql = "SELECT ". $field_name ." FROM ". $tbname .$wh;		$results = $this->db->query($sql)->result();		//echo $this->db->last_query();die;		foreach($results as $result){			$returnField = $result->$field_name;		}		return $returnField;	}	public function getUserInfo($mID){		$CI = & get_instance();		$returnField = '';		$this->db->select('m.status, m.email, m.username');		$this->db->from('members m');		$this->db->where('m.id', $mID);		$query = $this->db->get();		$results = $query->result();		//echo $this->db->last_query();die;		foreach($results as $result){			$returnField = $result;		}		return $returnField;	}}?>