<?php    class Sub2categories_model extends CI_Model    {	    public function __construct()	    {		    parent::__construct();	    }	    public function getList($id)	    {		    $cond = 'status = 1';		    $this->db->select('id,name,icon');		    $this->db->where('cate_id', $id);		    $query = $this->db->get('sub2categories');		    return $query->result();	    }	    public function getField($cateId, $id, $fieldName)	    {		    $cond = 'status = 1';		    $this->db->select($fieldName);		    $this->db->where('subcate_id', $id);		    $this->db->where('id', $id);		    $query = $this->db->get('sub2categories');		    return $query->result();	    }	    public function getAllCategories()	    {		    $this->db->select('id,name');		    $query = $this->db->get('sub2categories');		    $items[0] = '-- Select Category --';		    if ($query->num_rows() > 0) {			    foreach ($query->result() as $data) {				    $items[$data->id] = $data->name;			    }			    $query->free_result();			    return $items;		    }	    }    }?>