<?php    class Subcategories_model extends CI_Model    {    	public function __construct(){        	parent::__construct();        }	    public function getList($id)	    {		    $cond = 'status = 1';		    $this->db->select('id,name,icon');		    $this->db->where('cate_id', $id);		    $query = $this->db->get('subcategories');		    return $query->result();	    }	    public function getField($cateId, $id, $fieldName)	    {		    $cond = 'status = 1';		    $this->db->select($fieldName);		    $this->db->where('cate_id', $cateId);		    $this->db->where('id', $id);		    $query = $this->db->get('subcategories');		    //echo $this->db->last_query();		    return $query->result();	    }	    public function getAllCategories()	    {		    $this->db->select('id,name');		    $query = $this->db->get('subcategories');		    if ($query->num_rows() > 0) {			    foreach ($query->result() as $data) {				    $items[$data->id] = $data->name;			    }			    $query->free_result();			    return $items;		    }	    }    }?>