<?php    class Products_model extends CI_Model{    	public function __construct(){        	parent::__construct();        }        //get list via cate id 		public function getListProd($limit,$start,$id){		    $this->db->select('		    	prod.id, prod.title name, prod.created, prod.rank, prod.image, prod.status,		    	c.name catename, sc.name subcatename, sc2.name subcatename2		    ');			$this->db->from('products prod');			$this->db->join('categories c', 'c.id = prod.fcate_id', 'left');			$this->db->join('subcategories sc', 'sc.id = prod.cate_id', 'left');			$this->db->join('sub2categories sc2', 'sc2.id = prod.subcate_id', 'left');		   	$this->db->order_by('created', 'DESC');		   	$this->db->limit($limit,$start);		    $query = $this->db->get();		    if ($query->num_rows() > 0) {				//echo $this->db->last_query();				return $query->result();			}		    return false;		}		//get number of records via cate id		public function record_count(){			$this->db->from('products');			return $this->db->count_all_results();		}		public function delField($field,$id,$tbName){			$this->db->delete($tbName, array($field => $id));			return true;		}		public function addNew($datas,$tbName){			$this->db->insert($tbName, $datas);			return true;		}		//Get detail via id		public function getDetail($id){		    $this->db->select();		    $this->db->where('id', $id);		   	$query = $this->db->get('products');		   	return $query->result();		}		//Get more list via cate id and member id		public function getListProdMore($fields,$cateId,$memId){			$cond = 'status = 1';		    $this->db->select($fields);		    $this->db->where('status', 1);		    $this->db->where('cate_id', $cateId);		    $this->db->where('mem_id', $memId);		   	$this->db->order_by('created', 'DESC');			    $query = $this->db->get('asds');		    if ($query->num_rows() > 0) {				foreach ($query->result() as $row) {					$data[] = $row;				}				return $data;			}		    return false;		}    }?>